                            AuthorName = "ილია",
                            AuthorSurname = "ჭავჭავაძე",
                            ContentOfStory = "ილიას ,,მგზავრის წერილების” ახალგაზრდა ლირიკული გმირი თავისი ცხოვრების მეტად მნიშვნელოვან მიჯნასთან დგას და იმ პრინციპებს, მრწამსს აყალიბებს, რომლებმაც მისი შემდგომი არსებობა უნდა წარმართონ. იგი ფაქტობრივად საკუთარ ბედს წყვეტს, გადამწყვეტი პიროვნული არჩევანის აქტს ახორციელებს. თერგის და მყინვარის, დღის და ღამის სახეთა თითქოსდა აბსტრაქტული, რიტორიკული შეპირისპირებაც ამ პიროვნული არჩევანის აქტის, ილიას წინაშე წამოჭრილი ზნეობრივ-მსოფლმხედველობრივი დილემების გამომხატველია.\r\n\r\n\r\n,,მგზავრის წერილებში” თავდაპირველად ილია სატირიკოსად გვევლინება და სასტიკად დასცინის იმას, რასაც ერთმნიშვნელოვნად ემიჯნება – დაწინაურებულ ევროპასთან შედარებით აშკარად ჩამორჩენილ რუსულ სახელმწიფოს. ამ სახელმწიფოს ტიპიური ჩინოვნიკის ილიას მიერ შექმნილი კარიკატურული სახე ტევადია, მრავლისმთქმელია. მის კომიკურ ნაკვთებს შორის ყველაზე სასაცილო შოვინისტის და იმპერიალისტის მისიონერული, განმანათლებლურ-ცივილიზატორული ამბიციაა, ცრუ ინტელექტუალიზმი და პროგრესისტობაა, უბირი კაცის მჩატე თაყვანისცემაა მეცნიერების და განათლების მიმართ, მეცნიერ-გამომგონებლობის აბსურდული პრეტენზიაა.\r\nილიას თვალთახედვით, რუსეთის მიერ ზერელედ დანერგილი, სუროგატული განმანათლებლობის, ცივილიზებულობის და ევროპეიზმის ნაცვლად საქართველო ჭეშმარიტ განმანათლებლობას და პროგრესს, ევროპეიზმს უნდა ეზიაროს.\r\n\r\n\r\nილიას განამანათლებლური მრწამსის თანახმად, ყოველგვარი სოციალური და ეროვნული სატკივარის პირველმიზეზი გონების ძილია, უმეცრება და გაუნათლებლობაა, რის სიმბოლოსაც ,,მგზავრის წერილებში” ღამე წარმოადგენს. ღამე, ისევე როგორც რუსთა მიერ ნაქები ,,მამულის კვამლი”, გონების დამბინდველი ილუზიების განმასახიერებელიც არის, რომლებიც ზელს უშლის ქვეყნის მძიმე რეალობის ნათლად დანახვას, ,,მართალჭვრეტას” და გვარიგებს ამ რეალობასთან. გონების სიბნელე ხელსაყრელია ტირანებისთვის, რადგან უბირი ადამიანების მართვა უფრო იოლია. ამიტომ საქართველოს ხსნა გონების სინათლეშია, ნათელ, რეალისტურ აზროვნებაში და განათლებაშია, რასაც სიმბოლურად დღე განასახიერებს.\r\n\r\n\r\nდღე ამასთანავე განასახიერებს ინტენსიურ საზოგადოებრივ ცხოვრებასაც, განსხვავებით ღამისგან, რომელიც ილიას მიერ უარყოფილ, რომანტიზმის ერთ-ერთი ნაკადისთვის დამახასიათებელ მარტოობის კულტს, სამყაროსადმი მჭრეტველობით-პასიურ დამოკიდებულებას, სამყაროს ირაციონალურ, იდუმალ ძალთა პოეტიზაციას უკავშირდება. დინამიური და ენერგიული, ბრძოლით აღსავსე საზოგადოებრივი ცხოვრება, რომლის სიმბოლო დღის გარდა თერგიც არის, არამარტო მიმზიდველია, არამედ მტანჯველიცაა თავისი მშფოთვარებით – ამას ილია მკაფიოდ აცნობიერებს. და მაინც ის აკეთებს არჩევანს იმის სასარგებლოდ, რაც ქვეყნის განვითარების და გათავისუფლებისთვის არის აუცილებელი.\r\nისევე როგორც ,,დაკარგულ ედემში”, ,,მგზავრის წერილებშიც” თვით საქართველოს ბუნების სული მოითხოვს ქვეყნის თავისუფლებას. ამ თავისუფლებისმოყვარე სულის ხორცშესხმაა არაგვი ბარათაშვილის ,,ბედი ქართლისაში”. თუმცა ამ ორ ნაწარმოებს მხოლოდ ეს არა აქვს საერთო.\r\n,,მგზავრის წერილები” შეიძლება აღვიქვათ როგორც ილია ჭავჭავაძის პასუხი, რეაგირება ბარათაშვილის პოემაში გამოკვეთილ კითხვებზე და თვალსაზრისებზე. ილიას მიერ დახატული რუსი ოფიცერის სახე თითქოს რუსთა მმართველობის აუტანლობაზე ლეონიძის მსჯელობის ილუსტრაციაა. მშვიდობის თემა, რომელიც ერთ-ერთი საკვანძოა ლეონიძის და ერეკლეს კამათში, ,,მგზავრის წერილებშიც” საგანგებოდ აქცენტირებულია : ერეკლე რუსების მიერ საქართველოში მშვიდობის დამკვიდრებას იმედით მოელის, ხოლო ლელთ ღუნია თავისი ცხოვრებისეული გამოცდილების საფუძველზე თითქოს ერეკლეს ეკამათება და თვლის, რომ სხვათა მიერ დამყარებულ მშვიდობაზე უმჯობესი საკუთარი ცხოვრების წესის მიყოლა და სარისკო, მაგრამ ღირსებაზე დამყარებული თვითარსებობაა\r\n\r\n",
                            Title = "მგზავრის წერილები"
                        });
                });

            modelBuilder.Entity("BooksProject.Models.Users", b =>
                {
                    b.Property<int>("Id")
                        .ValueGeneratedOnAdd()
                        .HasColumnType("int");

                    SqlServerPropertyBuilderExtensions.UseIdentityColumn(b.Property<int>("Id"), 1L, 1);

                    b.Property<string>("EmailAddress")
                        .IsRequired()
                        .HasMaxLength(50)
                        .HasColumnType("nvarchar(50)");

                    b.Property<string>("Password")
                        .IsRequired()
                        .HasMaxLength(50)
                        .HasColumnType("nvarchar(50)");

                    b.HasKey("Id");

                    b.ToTable("users");
                });
#pragma warning restore 612, 618
        }
    }
}
